const navSlide=()=>{const e=document.querySelector(".burger"),t=document.querySelector(".hero__nav"),n=document.querySelectorAll(".hero__item");e.addEventListener("click",()=>{t.classList.toggle("list-active"),n.forEach((e,t)=>{e.style.animation?e.style.animation="":e.style.animation=`navLinkFade 0.5s ease forwards ${t/4+.4}s`})})};navSlide();